function OEFICode0($p) { if (!is_array($p)) return '';
	if (!$p[0]) return '--'; 
$diff = time()-$p[0]; if ($diff<0) $diff = 0;
if ($diff < 60*60) return floor($diff/60).'min';
if ($diff < 60*60*24) return floor($diff/60/60).'h';
//if ($diff < 60*60*24*132) return date('d/m', $p[0]);
$r = date('d/m/y', $p[0]);
return $r;//return floor($diff/(60*60*24)).$p[1];
} //in WE62f154128e..OEDynTag0.html1

