function OEFICode0($p) { if (!is_array($p)) return '';
	// generate list of genders, ex ",M,F"
// $p[0] is a list of gender values to store in DB
// $p[1] is a list of gender names to show in dropdown
// $p[2] is current selection, from DB or POST, ex 4
// $p[3] is name to show when no selection (may be empty)
// generate corresponding html code for options:
	$r = '';
	if (!$p[0]) return; 
	$p[0] = ','.$p[0]; // ",M,F"
	$p[1] = $p[3].','.$p[1]; // "(gender),M,F"
	$valSplit = explode(',', $p[0]);
	$namSplit = explode(',', $p[1]);
	foreach($valSplit as $i=>$value) {
		$name = $namSplit[$i];
		$value = ($value) ? $value : ''; // index, or empty string for first element
		$selected = ($value && $p[2] && $value == $p[2]) ? " selected='selected'" : ' ';
		$r .= "<option value='$value'$selected>$name</option>\r\n";
	}
	return $r;
} //in WE24694fe6f4..OEDynTag0.html1

function OEFICode1($p) { if (!is_array($p)) return '';
	// generate list of days, 1..28/29/30/31 depending on the month and year
// $p[0] is a text corresponding to empty value (when not yet selected) ex. "(day)"
// $p[1] is current selection, from DB or POST, ex 4
// generate corresponding html code for options:
	$r = '';
	$p[0] = ($p[0]) ? $p[0] : ''; // empty string if no value
	
	$dMax = 31;	// Don't limit max date in case month or year are changed - it needs to be done in js
	
	for ($i=0; $i<=$dMax; $i++) {
		$value = ($i) ? $i : ''; // index, or empty string for first element
		$name = ($i) ? $i : $p[0]; // index or default non-selection text
		$selected = ($i && $p[1] && $i == $p[1]) ? " selected='selected'" : ' ';
		$r .= "<option value='$value'$selected>$name</option>\r\n";
	}
	return $r;
} //in WEbd5b853dcd..OEDynTag0.html1

function OEFICode2($p) { if (!is_array($p)) return '';
	// $p[0] is a comma-separated list of month names: ex. "january,february,..,december"
// $p[1] is a text corresponding to empty value (when not yet selected) ex. "(months)"
// $p[2] is current selection, from DB or POST, ex 4
// generate corresponding html code for options:
	$r = '';
	if (!$p[0]) return; else $p[0] = $p[1].','.$p[0]; // "(month),January,.."
	$ySplit = explode(',', $p[0]); // ex ["(month)","January",..]
	foreach($ySplit as $i=>$name) {
		$value = ($i) ? $i : ''; // index, or empty string for first element
		$selected = ($value && $p[2] && $value == $p[2]) ? " selected='selected'" : ' ';
		$r .= "<option value='$value'$selected>$name</option>\r\n";
	}
	return $r;
} //in WE13bb53d1b8..OEDynTag0.html1

function OEFICode3($p) { if (!is_array($p)) return '';
	// $p[0] is a comma-separated list of years, with empty first element:
// ",2000,1999,..,1872"
// $p[1] is a text corresponding to empty value (when year not yet selected)
// generate corresponding html code for options:
	if (!$p[0]) return;
	$r = '';
	//$p[0] = $p[1].$p[0]; // "(year),2000,.."
	$ySplit = explode(',', $p[0]);
	foreach($ySplit as $y) {
		$yName = ($y) ? $y : $p[1]; // "(year)" for first item, or year number
		$selected = ($y && $p[2] && $y == $p[2]) ? ' selected="selected"' : ' ';
		$r .= "<option value=\"$y\"$selected>$yName</option>\r\n";
	}
	return $r;
} //in PK1!FI_YearOptionsGenerator

function OEFICode4($p) { if (!is_array($p)) return '';
	return ($p[0] && preg_match("/^.{6,20}$/", $p[0]));
} //in PK1!FINewPWValidator

function OEFICode5($p) { if (!is_array($p)) return '';
	if (!preg_match("/^\d\d\d\d$/", $p[0])) return null;
$month = $p[1]; if (strlen($month) < 2) $month = '0'.$month;
$day = $p[2]; if (strlen($day) < 2) $day = '0'.$day;
return $p[0].'/'.$month.'/'.$day;
} //in PK1!FIBirthDate

function OEFICode6($p) { if (!is_array($p)) return '';
	if (!$p[0] || !strpos($p[0], "@") || strpos($p[0], " ") !== false || strlen($p[0]) < 6) return null;
return 1;
} //in PK1!FIEmailValidator

