function oeAction_PK1_2_APHPYearListAuto($p, &$out) {
if (!($p[0]>=0 && $p[0]<=128)) return '';
$currYear = date('Y');$minYear = ($p[0]>0) ? $currYear-$p[0]+1 : $currYear;
$lastYear = $currYear-128;

$r='';for ($i=$minYear;$i>=$lastYear;$i--) $r.=','.$i;
return $r;
} //called by action PK1!2-APHPYearListAuto

